// affichage date et heure
exports.Time = function() {
	var now = new Date();
	var mois = parseInt(now.getMonth()) + parseInt(1);
	var Ret = now.getFullYear() +"/"+ mois +"/"+ now.getDate() +"-"+ now.getHours() +":"+ now.getMinutes() +":"+ now.getSeconds()+"."+ now.getMilliseconds();
	return Ret;
};

// fonction conversion caracteres
exports.Convert = function(string) {
	var Ret = string.replace(/%20/g, "\ ");
	Ret = Ret.replace(/%C3%A9/g, "é");
	Ret = Ret.replace(/e%CC%81/g, "é");
	Ret = Ret.replace(/%C3%AA/g, "ê");
	Ret = Ret.replace(/e%CC%82/g, "ê");
	Ret = Ret.replace(/e%CC%80/g, "è");
	Ret = Ret.replace(/%C3%A8/g, "è");
	Ret = Ret.replace(/%C3%AB/g, "ë");
	Ret = Ret.replace(/%C3%A0/g, "à");
	Ret = Ret.replace(/%C3%80/g, "À");
	Ret = Ret.replace(/%C3%A2/g, "â");
	Ret = Ret.replace(/%C3%A7/g, "ç");
	Ret = Ret.replace(/%C3%B1/g, "ñ");
	Ret = Ret.replace(/%C3%BB/g, "û");
	Ret = Ret.replace(/%C3%B9/g, "ù");
	Ret = Ret.replace(/%C3%AF/g, "ï");
	return Ret;
};

// fonction remplacement caracteres
exports.Replace = function(string) {
	var Ret = string.replace(/à/g, "a");
	Ret = Ret.replace(/\ /g, "_");
	Ret = Ret.replace(/'/g, "_");
	Ret = Ret.replace(/ç/g, "c");
	Ret = Ret.replace(/é/g, "e");
	Ret = Ret.replace(/è/g, "g");
	Ret = Ret.replace(/ê/g, "g");
	Ret = Ret.replace(/ë/g, "e");
	Ret = Ret.replace(/î/g, "i");
	Ret = Ret.replace(/ï/g, "i");
	Ret = Ret.replace(/ô/g, "g");
	Ret = Ret.replace(/ö/g, "g");
	Ret = Ret.replace(/ù/g, "u");
	Ret = Ret.replace(/ü/g, "u");
	Ret = Ret.replace(/Â/g, "A");
	Ret = Ret.replace(/Ç/g, "C");
	Ret = Ret.replace(/É/g, "E");
	Ret = Ret.replace(/È/g, "E");
	Ret = Ret.replace(/Ê/g, "g");
	Ret = Ret.replace(/Ë/g, "E");
	Ret = Ret.replace(/Î/g, "I");
	Ret = Ret.replace(/Ï/g, "I");
	Ret = Ret.replace(/Ô/g, "O");
	Ret = Ret.replace(/Ö/g, "O");
	Ret = Ret.replace(/Ù/g, "U");
	Ret = Ret.replace(/Ü/g, "U");
	Ret = Ret.replace(/\[/g, "");
	Ret = Ret.replace(/\]/g, "");
	Ret = Ret.replace(/\(/g, "");
	Ret = Ret.replace(/\)/g, "");
	Ret = Ret.replace(/\//g, "-");
	Ret = Ret.replace(/\?/g, "-");
	return Ret;
};